"use client";

import { useCartStore } from "@/store/cartStore";
import { X, Trash2, Minus, Plus } from "lucide-react";
import Image from "next/image";
import Link from "next/link";
import { useEffect, useState } from "react";

export default function CartDrawer() {
    const {
        isOpen,
        closeCart,
        items,
        removeItem,
        updateQuantity,
        getCartTotal
    } = useCartStore();

    const [mounted, setMounted] = useState(false);

    // Prevent hydration mismatch
    useEffect(() => {
        setMounted(true);
    }, []);

    if (!mounted) return null;

    return (
        <>
            {/* Backdrop */}
            <div
                className={`fixed inset-0 bg-black/50 z-[60] transition-opacity duration-300 ${isOpen ? 'opacity-100' : 'opacity-0 pointer-events-none'}`}
                onClick={closeCart}
            />

            {/* Drawer */}
            <div
                className={`fixed inset-y-0 right-0 z-[70] w-full max-w-md bg-white shadow-xl transform transition-transform duration-300 ease-in-out ${isOpen ? 'translate-x-0' : 'translate-x-full'}`}
            >
                <div className="flex flex-col h-full">
                    {/* Header */}
                    <div className="flex items-center justify-between px-6 py-4 border-b border-zinc-200">
                        <h2 className="text-lg font-bold text-zinc-900">Shopping Cart</h2>
                        <button onClick={closeCart} className="p-2 text-zinc-500 hover:text-zinc-900 rounded-full hover:bg-zinc-100">
                            <X size={24} />
                        </button>
                    </div>

                    {/* Items */}
                    <div className="flex-1 overflow-y-auto px-6 py-4 space-y-6">
                        {items.length === 0 ? (
                            <div className="h-full flex flex-col items-center justify-center text-center text-zinc-500">
                                <p className="text-lg font-medium mb-2">Your cart is empty</p>
                                <button onClick={closeCart} className="text-zinc-900 underline hover:no-underline">
                                    Continue Shopping
                                </button>
                            </div>
                        ) : (
                            items.map((item) => (
                                <div key={`${item.id}-${item.variantId}`} className="flex gap-4">
                                    <div className="relative h-24 w-24 flex-shrink-0 overflow-hidden rounded-md border border-zinc-200">
                                        {item.image ? (
                                            <Image
                                                src={item.image}
                                                alt={item.title}
                                                fill
                                                className="object-cover object-center"
                                            />
                                        ) : (
                                            <div className="w-full h-full bg-zinc-100 flex items-center justify-center text-xs text-zinc-400">No Image</div>
                                        )}
                                    </div>

                                    <div className="flex flex-1 flex-col">
                                        <div>
                                            <div className="flex justify-between text-base font-medium text-zinc-900">
                                                <Link href={`/products/${item.handle}`} onClick={closeCart} className="hover:text-blue-600 line-clamp-1">
                                                    {item.title}
                                                </Link>
                                                <p className="ml-4">${(item.price * item.quantity).toFixed(2)}</p>
                                            </div>
                                            {item.variantTitle && (
                                                <p className="mt-1 text-sm text-zinc-500">{item.variantTitle}</p>
                                            )}
                                        </div>

                                        <div className="flex flex-1 items-end justify-between text-sm">
                                            <div className="flex items-center border border-zinc-200 rounded-md">
                                                <button
                                                    onClick={() => updateQuantity(item.id, item.variantId, item.quantity - 1)}
                                                    className="p-1.5 hover:bg-zinc-100 text-zinc-600 disabled:opacity-50"
                                                    disabled={item.quantity <= 1}
                                                >
                                                    <Minus size={14} />
                                                </button>
                                                <span className="px-2 font-medium w-8 text-center">{item.quantity}</span>
                                                <button
                                                    onClick={() => updateQuantity(item.id, item.variantId, item.quantity + 1)}
                                                    className="p-1.5 hover:bg-zinc-100 text-zinc-600"
                                                >
                                                    <Plus size={14} />
                                                </button>
                                            </div>

                                            <button
                                                type="button"
                                                onClick={() => removeItem(item.id, item.variantId)}
                                                className="font-medium text-red-600 hover:text-red-500 flex items-center gap-1"
                                            >
                                                <Trash2 size={16} />
                                                <span className="hidden sm:inline">Remove</span>
                                            </button>
                                        </div>
                                    </div>
                                </div>
                            ))
                        )}
                    </div>

                    {/* Footer */}
                    {items.length > 0 && (
                        <div className="border-t border-zinc-200 px-6 py-6 bg-zinc-50">
                            <div className="flex justify-between text-base font-medium text-zinc-900 mb-4">
                                <p>Subtotal</p>
                                <p>${getCartTotal().toFixed(2)}</p>
                            </div>
                            <p className="mt-0.5 text-sm text-zinc-500 mb-6">
                                Shipping and taxes calculated at checkout.
                            </p>
                            <div className="mt-6">
                                <a
                                    href="#"
                                    className="flex items-center justify-center rounded-md border border-transparent bg-zinc-900 px-6 py-3 text-base font-medium text-white shadow-sm hover:bg-zinc-800"
                                >
                                    Checkout
                                </a>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </>
    );
}

