"use client";

import { ProductImage } from "@/types";
import Image from "next/image";
import { useState, useEffect } from "react";

interface ProductGalleryProps {
    images: ProductImage[];
    title: string;
    variantImageId?: number | null;
}

export default function ProductGallery({ images, title, variantImageId }: ProductGalleryProps) {
    const [selectedImage, setSelectedImage] = useState(images[0]);

    // Update main image if variant changes
    useEffect(() => {
        if (variantImageId) {
            const variantImg = images.find(img => img.id === variantImageId);
            if (variantImg) setSelectedImage(variantImg);
        }
    }, [variantImageId, images]);

    if (images.length === 0) {
        return (
            <div className="aspect-square bg-zinc-100 rounded-2xl flex items-center justify-center text-zinc-400">
                No Image Available
            </div>
        );
    }

    return (
        <div className="space-y-6">
            <div className="relative aspect-square bg-zinc-50 rounded-2xl overflow-hidden border border-zinc-200 group">
                <Image
                    src={selectedImage.src}
                    alt={title}
                    fill
                    className="object-contain object-center p-8 transition-transform duration-500 group-hover:scale-105"
                    sizes="(max-width: 768px) 100vw, 50vw"
                    priority
                />
            </div>

            <div className="grid grid-cols-5 gap-3">
                {images.map((img) => (
                    <button
                        key={img.id}
                        onClick={() => setSelectedImage(img)}
                        className={`relative aspect-square rounded-lg overflow-hidden border-2 transition-all ${selectedImage.id === img.id
                                ? 'border-zinc-900 ring-2 ring-zinc-900/10'
                                : 'border-zinc-200 hover:border-zinc-400'
                            }`}
                    >
                        <Image
                            src={img.src}
                            alt={title}
                            fill
                            className="object-cover p-1"
                            sizes="10vw"
                        />
                    </button>
                ))}
            </div>
        </div>
    );
}

"use client";

import { Product, ProductVariant } from "@/types";
import { useState } from "react";
import ProductGallery from "./ProductGallery";
import ProductOptions from "./ProductOptions";
import ProductActions from "./ProductActions";
import { Shield, AlertCircle, Check, Truck } from "lucide-react";

interface ProductMainContentProps {
    product: Product;
}

export default function ProductMainContent({ product }: ProductMainContentProps) {
    const [selectedVariant, setSelectedVariant] = useState<ProductVariant>(product.variants[0]);
    const [activeTab, setActiveTab] = useState<'description' | 'specs' | 'shipping'>('description');

    return (
        <div className="grid grid-cols-1 lg:grid-cols-12 gap-12">
            {/* Left: Gallery */}
            <div className="lg:col-span-7">
                <ProductGallery
                    images={product.images}
                    title={product.title}
                    variantImageId={selectedVariant?.image_id}
                />
            </div>

            {/* Right: Info & Actions */}
            <div className="lg:col-span-5 flex flex-col">
                <div className="mb-2">
                    {product.vendor && (
                        <span className="text-zinc-500 text-sm font-bold uppercase tracking-widest">{product.vendor}</span>
                    )}
                </div>

                <h1 className="text-3xl md:text-4xl font-black text-zinc-900 leading-tight mb-4 tracking-tight">
                    {product.title}
                </h1>

                <div className="flex items-baseline gap-3 mb-8">
                    <span className="text-4xl font-black text-zinc-900">
                        ${selectedVariant?.price || '0.00'}
                    </span>
                    {selectedVariant?.compare_at_price && (
                        <span className="text-xl text-zinc-400 line-through font-medium">
                            ${selectedVariant.compare_at_price}
                        </span>
                    )}
                    <span className="text-zinc-500 font-medium">USD</span>
                </div>

                {/* Options Selection */}
                <ProductOptions
                    product={product}
                    onVariantChange={setSelectedVariant}
                />

                {/* Status Badges */}
                <div className="flex flex-wrap gap-4 mb-8">
                    <div className="flex items-center text-emerald-600 bg-emerald-50 px-3 py-1.5 rounded-full text-xs font-bold uppercase tracking-wider border border-emerald-100">
                        <Check size={14} className="mr-1.5" /> In Stock
                    </div>
                    <div className="flex items-center text-indigo-600 bg-indigo-50 px-3 py-1.5 rounded-full text-xs font-bold uppercase tracking-wider border border-indigo-100">
                        <Truck size={14} className="mr-1.5" /> Free Ground Shipping
                    </div>
                </div>

                {/* Cart Actions */}
                <div className="bg-white p-6 rounded-2xl border-2 border-zinc-100 shadow-sm mb-10">
                    <ProductActions product={product} selectedVariant={selectedVariant} />
                    <p className="text-[10px] text-zinc-400 mt-4 text-center uppercase tracking-widest font-bold">
                        Industrial Grade ΓÇó 30-Day Returns ΓÇó Bulk Discounts Available
                    </p>
                </div>

                {/* Tabbed Info */}
                <div className="mt-auto border-t border-zinc-100 pt-10">
                    <div className="flex gap-8 border-b border-zinc-100 mb-6">
                        {(['description', 'specs', 'shipping'] as const).map((tab) => (
                            <button
                                key={tab}
                                onClick={() => setActiveTab(tab)}
                                className={`pb-4 text-sm font-bold uppercase tracking-widest transition-all relative ${activeTab === tab ? 'text-zinc-900' : 'text-zinc-400 hover:text-zinc-600'
                                    }`}
                            >
                                {tab}
                                {activeTab === tab && (
                                    <div className="absolute bottom-0 left-0 right-0 h-1 bg-zinc-900 transition-all" />
                                )}
                            </button>
                        ))}
                    </div>

                    <div className="min-h-[200px]">
                        {activeTab === 'description' && (
                            <div
                                className="prose prose-sm prose-zinc max-w-none text-zinc-600 leading-relaxed"
                                dangerouslySetInnerHTML={{ __html: product.body_html }}
                            />
                        )}
                        {activeTab === 'specs' && (
                            <div className="space-y-4">
                                <div className="grid grid-cols-2 py-2 border-b border-zinc-50">
                                    <span className="text-zinc-500 text-sm">Product Type</span>
                                    <span className="text-zinc-900 text-sm font-semibold">{product.product_type}</span>
                                </div>
                                <div className="grid grid-cols-2 py-2 border-b border-zinc-50">
                                    <span className="text-zinc-500 text-sm">Vendor</span>
                                    <span className="text-zinc-900 text-sm font-semibold">{product.vendor}</span>
                                </div>
                                <div className="grid grid-cols-2 py-2 border-b border-zinc-50">
                                    <span className="text-zinc-500 text-sm">SKU</span>
                                    <span className="text-zinc-900 text-sm font-semibold">{selectedVariant?.sku || 'N/A'}</span>
                                </div>
                                {product.tags.length > 0 && (
                                    <div className="grid grid-cols-2 py-2">
                                        <span className="text-zinc-500 text-sm">Tags</span>
                                        <div className="flex flex-wrap gap-1">
                                            {product.tags.slice(0, 5).map(tag => (
                                                <span key={tag} className="text-[10px] bg-zinc-100 text-zinc-600 px-1.5 py-0.5 rounded">{tag}</span>
                                            ))}
                                        </div>
                                    </div>
                                )}
                            </div>
                        )}
                        {activeTab === 'shipping' && (
                            <div className="space-y-4 text-zinc-600 text-sm leading-relaxed">
                                <p>Standard shipping arrives in 5-7 business days. Priority shipping available at checkout.</p>
                                <div className="flex items-start gap-3 mt-4">
                                    <Shield size={18} className="text-zinc-400 mt-1" />
                                    <p>Your purchase is protected by our industrial-grade quality guarantee. Every item is inspected for durability and performance.</p>
                                </div>
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
}

"use client";

import { Product, ProductVariant, ProductOption } from "@/types";
import { useState, useEffect } from "react";

interface ProductOptionsProps {
    product: Product;
    onVariantChange: (variant: ProductVariant) => void;
}

export default function ProductOptions({ product, onVariantChange }: ProductOptionsProps) {
    // Map of option name to selected value
    const [selectedOptions, setSelectedOptions] = useState<Record<string, string>>(() => {
        const initial: Record<string, string> = {};
        product.options.forEach(option => {
            if (option.values.length > 0) {
                initial[option.name] = option.values[0];
            }
        });
        return initial;
    });

    // Find the current variant matches selected options
    useEffect(() => {
        const matchedVariant = product.variants.find(variant => {
            return product.options.every(option => {
                const optKey = `option${option.position}` as keyof ProductVariant;
                return variant[optKey] === selectedOptions[option.name];
            });
        });

        if (matchedVariant) {
            onVariantChange(matchedVariant);
        }
    }, [selectedOptions, product, onVariantChange]);

    const handleOptionChange = (optionName: string, value: string) => {
        setSelectedOptions(prev => ({
            ...prev,
            [optionName]: value
        }));
    };

    if (product.variants.length <= 1 && product.variants[0]?.title === "Default Title") {
        return null;
    }

    return (
        <div className="space-y-6 mb-8">
            {product.options.map((option) => (
                <div key={option.id}>
                    <h3 className="text-sm font-bold text-zinc-900 mb-3">{option.name}</h3>
                    <div className="flex flex-wrap gap-2">
                        {option.values.map((value) => {
                            const isSelected = selectedOptions[option.name] === value;
                            return (
                                <button
                                    key={value}
                                    onClick={() => handleOptionChange(option.name, value)}
                                    className={`px-4 py-2 text-sm border-2 rounded-lg transition-all ${isSelected
                                            ? 'border-zinc-900 bg-zinc-900 text-white font-bold'
                                            : 'border-zinc-200 bg-white text-zinc-600 hover:border-zinc-300'
                                        }`}
                                >
                                    {value}
                                </button>
                            );
                        })}
                    </div>
                </div>
            ))}
        </div>
    );
}
